import { DataTypes } from 'sequelize';
import sequelize from '../config/database';
import Profile from './profile.model';

const Follow = sequelize.define('Follow', {
  id: {
    type: DataTypes.UUID,
    defaultValue: DataTypes.UUIDV4,
    allowNull: false,
    primaryKey: true
  },
  followerId: {
    type: DataTypes.UUID,
    allowNull: false,
